mapper.insert:
insert into work (work_id, process_number, product_id, 
  process_id, device_id, rating
  )
values (#{workId,jdbcType=VARCHAR}, #{processNumber,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, 
  #{processId,jdbcType=VARCHAR}, #{deviceId,jdbcType=VARCHAR}, #{rating,jdbcType=INTEGER}
  )

mapper.select:
SELECT * FROM work
WHERE work_id LIKE CONCAT(CONCAT('%',#{workId}),'%')
limit #{limit}
offset #{offset}

SELECT * FROM work
WHERE product_id LIKE CONCAT(CONCAT('%',#{productId}),'%')
limit #{limit}
offset #{offset}

SELECT * FROM work
WHERE process_id LIKE CONCAT(CONCAT('%',#{processId}),'%')
limit #{limit}
offset #{offset}

SELECT * FROM work
WHERE device_id LIKE CONCAT(CONCAT('%',#{deviceId}),'%')
limit #{limit}
offset #{offset}
